import React, { Fragment, useContext } from 'react'
import ReactDOM from 'react-dom'
import Context from '../../store.Context'


const Overlay = (props) => {
    return <div className='p-4 rounded-xl shadow-2xl z-30 fixed top-[20rem] left-[40%] w-[25%] m-auto bg-white'>
        <div className='content'>{props.children}</div>
    </div>
}
const BackDrop = () => {
  const {closeModal} = useContext(Context)
    return <div onClick={closeModal} className='bg-black opacity-75 top-0 left-0 z-20  fixed w-[100%] h-[100vh] bg-[ rgba(0, 0, 0, 0.75)]'>
    </div>
}

const Modal = (props) => {
  return (
    <Fragment>
      {ReactDOM.createPortal(<BackDrop/>, document.getElementById('overlay-root'))}
      {ReactDOM.createPortal(<Overlay>{props.children}</Overlay>, document.getElementById('overlay-root'))}
    </Fragment>
  )
}

export default Modal
