import React, { Fragment } from 'react'

const Overlay = (props) => {
    return <div className='fixed top-[20vh] left-[5%] w-[90%] bg-white p-4 rounded-lg shadow-xl z-30 animation-[slide_down-300ms-ease-out-forwards]'>
        <div className='content'>{props.children}</div>
    </div>
}
const BackDrop = (props) => {
    return <div className='top-0 left-0 z-20  fixed w-[100%] h-[100vh] bg-[ rgba(0, 0, 0, 0.75)]'>
        
    </div>
}

const Modal = (props) => {
  return (
    <Fragment>
      {ReactDOM.createPortal(<BackDrop/>, document.getElementById('overlay-root'))}
      {ReactDOM.createPortal(<Overlay>{props.children}</Overlay>, document.getElementById('overlay-root'))}
    </Fragment>
  )
}

export default Modal
