import React, { useContext } from "react";
import Modal from "../UI/Modal";
import Context from '../../store/Context'
const Cart = (props) => {
  const cartCtx = useContext(Context)
  const cartAmount = cartCtx.totalAmount.toFixed(2)
  const cartItems = (
    <ul>
      {
        cartCtx.items.map((element) => {
          return <li className="flex justify-between" key={element.id}>{element.name} <span>X</span></li>
        })
      }
    </ul>
  );
  return (
    <Modal close={props.handleCloseClick}>
    <div className="list-none m-0 p-0 overflow-auto">
      {cartItems}
      <div className="flex justify-between items-center font-bold text-xl my-4">
        <span>Total Amount</span>
        <span>{cartItems.totalAmount}</span>
      </div>
      <div className="text-right">
        <button onClick={props.handleCloseClick} className="hover:bg-[#5a1a01] hover:border-[#5a1a01] hover:text-white ml-4 border-[1px] border-[#8a2b06] py-1 px-8 rounded-xl bg-transparent text-[#8a2b06]">Close</button>
        <button className="hover:bg-[#5a1a01] hover:border-[#5a1a01] hover:text-white ml-4 border-[#8a2b06] py-1 px-8 rounded-xl bg-[#8a2b06] text-white">Order</button>
      </div>
    </div>
    </Modal>
  );
};

export default Cart;
