import React, { useState } from "react";
import Input from "../UI/Input";
const MealItemFrom = (props) => {
  const [userInput, setUserInput] = useState({amount: 0, id: ''});

  const handleChange = (e) => {
    setUserInput({amount: e.target.value, id: e.target.id})
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(userInput)
  }
  return (
    <form onSubmit={handleSubmit} type="submit" className="p-4">
      <Input
        className="flex items-center mb-2"
        label="Amount"
        input={{
          onChange: handleChange,
          defaultValue: userInput.amount,
          id: "amount_" + props.id,
          type: "number",
          min: "1",
          max: "5",
          step: "1",
        }}
      />
      <button
        type="submit"
        className="hover:bg-[#641e03] hover:border-[#641e03] font-bold bg-[#8a2b06] text-white rounded-2xl px-8 py-1"
      >
        + Add
      </button>
    </form>
  );
};

export default MealItemFrom;
