import React, { useState } from "react";
import Input from "../UI/Input";
const MealItemFrom = (props) => {
  const [userInput, setUserInput] = useState({ amount: 0 });
  const handleChange = (e) => {
    setUserInput(e.target.value)
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(userInput)
  }
  return (
    <form type="submit" className="p-4">
      <Input
        onChange={handleChange}
        className="flex items-center mb-2"
        label="Amount"
        input={{
          id: "amount_" + props.id,
          type: "number",
          min: "1",
          max: "5",
          defaultValue: "1",
          step: "1",
        }}
      />
      <button
        type="submit"
        className="hover:bg-[#641e03] hover:border-[#641e03] font-bold bg-[#8a2b06] text-white rounded-2xl px-8 py-1"
      >
        + Add
      </button>
    </form>
  );
};

export default MealItemFrom;
